(2, 1, '2016-08-19 11:02:24', '2016-08-19 03:02:24', '[av_layerslider id=''1'']\r\n\r\n[av_layout_row border='''' min_height=''0'' color=''main_color'' mobile=''av-flex-cells'' id=''homepage-about-block'']\r\n[av_cell_one_full vertical_align=''top'' padding=''70px,0px,70px,0px'' background_color='''' src=''http://localhost/baodao/wp-content/uploads/2016/08/bg_02.jpg'' attachment=''47'' attachment_size=''full'' background_attachment=''scroll'' background_position=''top left'' background_repeat=''no-repeat'' mobile_display='''']\r\n\r\n[av_textblock size='''' font_color='''' color='''']\r\n<h2 style="text-align: center;">我们为您量身订制您的塔斯马尼亚行程</h2>\r\n[/av_textblock]\r\n\r\n[av_one_third first min_height='''' vertical_alignment=''av-align-top'' space='''' margin=''0px'' margin_sync=''true'' padding=''0px'' padding_sync=''true'' border='''' border_color='''' radius=''0px'' radius_sync=''true'' background_color='''' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'' animation='''' mobile_display='''']\r\n\r\n[av_textblock size=''16'' font_color='''' color='''']\r\n<h4 style="text-align: center;">塔州当地旅行社</h4>\r\n\r\n<hr />\r\n<p style="text-align: center;">我们居住生活在塔州</p>\r\n<p style="text-align: center;">我们最了解塔斯马尼亚</p>\r\n\r\n\r\n<hr />\r\n\r\n[/av_textblock]\r\n\r\n[/av_one_third][av_one_third min_height='''' vertical_alignment='''' space='''' custom_margin='''' margin=''0px'' padding=''0px'' border='''' border_color='''' radius=''0px'' background_color='''' src='''' background_position=''top left'' background_repeat=''no-repeat'' animation='''' mobile_display='''']\r\n\r\n[av_textblock size=''16'' font_color='''' color='''']\r\n<h4 style="text-align: center;">独一无二的体验</h4>\r\n\r\n<hr />\r\n<p style="text-align: center;">我们有中英双语优秀导游</p>\r\n<p style="text-align: center;">我们有丰富多彩的行程</p>\r\n\r\n\r\n<hr />\r\n\r\n[/av_textblock]\r\n\r\n[/av_one_third][av_one_third min_height='''' vertical_alignment='''' space='''' custom_